	    console.log(`setting ${key} ...`);
	    return Reflect.set(target, key, value, receiver);
	  }
	});

	obj.count = 1;            //setting count ...
	++obj.count;              //getting count ...
	                          //setting count ...
	console.log(obj.count);   //getting count ...
	                          //2
</script>


