	console.log(window.c);//undefined

	l = l * l;//4
	c = c * c;//typeError 不能被修改

	for( let i = 0; i < arr.length; i++){}
	console.log(i)//ReferenceError 

	// 函数不具有块级作用域
	function a(){
		console.log(a.name)
	}
