	                if ( list[ firingIndex ].apply( context, args ) === false && flags.stopOnFalse ) {
	                    memory = true; // Mark as halted
	                    break;
	                }
	            }
	            firing = false;
	            if ( list ) {
	                if ( !flags.once ) {
	                    if ( stack && stack.length ) {
	                        memory = stack.shift();
	                        self.fireWith( memory[ 0 ], memory[ 1 ] );
	                    }
